import React from "react";
import Particles from "react-tsparticles";
import { loadFull } from "tsparticles";

const InteractiveBackground = () => {
    const particlesInit = async (main) => {
        await loadFull(main);
    };

    return (
        <div className="relative w-full h-screen overflow-hidden">
            {/* Particles Background */}
            <Particles
                id="tsparticles"
                init={particlesInit}
                options={{
                    background: { color: "#000000" },
                    particles: {
                        number: { value: 100 },
                        color: { value: "#ffffff" },
                        shape: { type: "circle" },
                        opacity: { value: 0.7 },
                        size: { value: 5 },
                        move: {
                            enable: true,
                            speed: 3,
                            direction: "random",
                            random: true,
                            straight: false,
                            outModes: "out",
                        },
                    },
                }}
            />

            {/* Header */}
            <header className="absolute w-full top-0 left-0 p-6 bg-gradient-to-r from-blue-500 to-indigo-600 text-white shadow-lg z-10">
                <div className="flex items-center justify-between">
                    <div className="text-3xl font-bold">ChatBot</div>
                    <nav className="flex space-x-6">
                        <a href="#about" className="hover:text-gray-200 transition">About</a>
                        <a href="#features" className="hover:text-gray-200 transition">Features</a>
                        <a href="#contact" className="hover:text-gray-200 transition">Contact</a>
                    </nav>
                </div>
            </header>

            {/* Main Chatbot UI */}
            <div className="absolute inset-0 flex items-center justify-center z-10">
                <div className="p-8 bg-white rounded-3xl shadow-2xl w-96">
                    <div className="text-center text-lg font-semibold mb-4">
                        Chatbot UI
                    </div>
                    <div className="p-4 bg-gray-200 rounded-lg shadow-md">
                        <div className="bot-message message">
                            <div className="text text-gray-700">
                                Hello! How can I help you today?
                            </div>
                        </div>
                    </div>
                </div>
            </div>

            {/* Footer */}
            <footer className="absolute bottom-0 w-full p-6 bg-gradient-to-r from-indigo-600 to-blue-500 text-white text-center">
                <p className="text-sm">Â© 2025 ChatBot. All rights reserved.</p>
                <div className="mt-2">
                    <a href="https://github.com" className="hover:text-gray-200 transition">GitHub</a>
                    <span className="mx-2">|</span>
                    <a href="mailto:support@example.com" className="hover:text-gray-200 transition">Support</a>
                </div>
            </footer>
        </div>
    );
};

export default InteractiveBackground;
