import React from "react";
import Particles from "react-tsparticles";
import { loadFull } from "tsparticles";

const InteractiveBackground = () => {
    const particlesInit = async (main) => {
        await loadFull(main);
    };

    return (
        <div className="relative w-full h-screen bg-black overflow-hidden">
            {/* Particles Background */}
            <Particles
                id="tsparticles"
                init={particlesInit}
                options={{
                    background: { color: "#000000" },
                    particles: {
                        number: { value: 80 }, // Decreased for a more minimalistic look
                        color: { value: "#ffffff" },
                        shape: { type: "circle" },
                        opacity: { value: 0.5 },
                        size: { value: 5 }, // Increased size for better visibility
                        move: {
                            enable: true,
                            speed: 3, // Increased speed for more dynamic movement
                            direction: "random",
                            random: true,
                            straight: false,
                            outModes: "out",
                        },
                    },
                }}
            />

            {/* Chatbot UI */}
            <div className="absolute inset-0 flex items-center justify-center z-10">
                <div className="p-8 bg-white rounded-3xl shadow-2xl w-96">
                    <div className="text-center text-lg font-semibold mb-4">
                        Chatbot UI
                    </div>
                    <div className="p-4 bg-gray-200 rounded-lg shadow-md">
                        <div className="bot-message message">
                            <div className="text text-gray-700">
                                Hello! How can I help you today?
                            </div>
                        </div>
                        {/* User message will go here */}
                    </div>
                </div>
            </div>
        </div>
    );
};

export default InteractiveBackground;
