import React from 'react';
import Particles from 'react-tsparticles';
import { loadFull } from 'tsparticles';

const InteractiveBackground = () => {
    const particlesInit = async (main) => {
        await loadFull(main);
    };

    return (
        <div className="relative w-full h-screen bg-black">
            {/* Particles Background */}
            <Particles
                id="tsparticles"
                init={particlesInit}
                options={{
                    background: {
                        color: "#000000", // Space black background
                    },
                    particles: {
                        number: {
                            value: 100,
                            density: {
                                enable: true,
                                value_area: 800,
                            },
                        },
                        color: {
                            value: "#ffffff", // White particles (stars)
                        },
                        shape: {
                            type: "circle", // Particles are circular (stars)
                        },
                        opacity: {
                            value: 0.5, // Particles' opacity
                            random: true, // Random opacity for stars
                        },
                        size: {
                            value: 2, // Size of particles (stars)
                            random: true, // Random sizes for more variety
                        },
                        move: {
                            enable: true, // Make particles move
                            speed: 0.5, // Speed of particle movement
                            direction: "none", // No specific direction
                            random: true, // Random movement
                            outModes: "out", // Particles leave the area if they go out
                        },
                    },
                }}
            />

            {/* Chatbot UI Placeholder */}
            <div className="absolute inset-0 flex items-center justify-center z-10">
                <div className="p-6 bg-white rounded-lg shadow-lg opacity-80">
                    Your Chatbot UI Here
                </div>
            </div>
        </div>
    );
};

export default InteractiveBackground;
